mapper.select:
select 

from REPORT_PLATFORMAREA


	and PLATFORMAREA_ID = #{platformAreaId,jdbcType=DECIMAL}


	and AREA_OPERATING_UNIT_SHORTNAME = #{areaOperatingUnitShortName,jdbcType=VARCHAR}


	and THE_PLATFORMAREA_SHORTNAME = #{thePlatFormAreaShortName,jdbcType=VARCHAR}


	and THE_BUSINESS_DEPARTMENT = #{theBusinessDepartment,jdbcType=VARCHAR}


	and THE_AREA = #{theArea,jdbcType=VARCHAR}


	and ACTIVE = #{active,jdbcType=VARCHAR}


order by CREATE_TIME desc

select count(*)
from REPORT_PLATFORMAREA


	and PLATFORMAREA_ID = #{platformAreaId,jdbcType=DECIMAL}


	and AREA_OPERATING_UNIT_SHORTNAME = #{areaOperatingUnitShortName,jdbcType=VARCHAR}


	and THE_PLATFORMAREA_SHORTNAME = #{thePlatFormAreaShortName,jdbcType=VARCHAR}


	and THE_BUSINESS_DEPARTMENT = #{theBusinessDepartment,jdbcType=VARCHAR}


	and THE_AREA = #{theArea,jdbcType=VARCHAR}


	and ACTIVE = #{active,jdbcType=VARCHAR}
