mapper.insert:
INSERT INTO YILIU_VEHICLES_INFO SELECT SEQ_YILIU_VEHICLEINFO_ID.NEXTVAL ID, A.*, TRUNC(SYSDATE,'MI') RECORD_DATE FROM(
(SELECT #{item.vehicle,jdbcType=VARCHAR} VEHICLE, #{item.vehicleID,jdbcType=VARCHAR} VEHICLEID, #{item.gpsTime,jdbcType=DATE} GPSTIME, #{item.speed,jdbcType=DOUBLE} SPEED, #{item.odometer,jdbcType=DOUBLE} ODOMETER, #{item.lat,jdbcType=DOUBLE} LAT, #{item.lon,jdbcType=DOUBLE} LON, #{item.direction,jdbcType=INTEGER} DIRECTION, #{item.status,jdbcType=VARCHAR} STATUS, #{item.placeName,jdbcType=VARCHAR} PLACENAME, #{item.province,jdbcType=VARCHAR} PROVICE, #{item.city,jdbcType=VARCHAR} CITY, #{item.district,jdbcType=VARCHAR} DISTRICT, #{item.roadName,jdbcType=VARCHAR} ROADNAME, #{item.t1,jdbcType=INTEGER} T1, #{item.t2,jdbcType=INTEGER} T2, #{item.t3,jdbcType=INTEGER} T3, #{item.t4,jdbcType=INTEGER} T4, #{item.ht1,jdbcType=INTEGER} HT1, #{item.ht2,jdbcType=INTEGER} HT2, #{item.ht3,jdbcType=INTEGER} HT3, #{item.ht4,jdbcType=INTEGER} HT4, #{item.lat02,jdbcType=DOUBLE} LAT02, #{item.lon02,jdbcType=DOUBLE} LON02, #{item.lat,jdbcType=VARCHAR} AREANAME, #{item.lat,jdbcType=VARCHAR} TIME1, #{item.lat,jdbcType=VARCHAR} TIME2, #{item.lat,jdbcType=VARCHAR} TIME3, #{item.lat,jdbcType=VARCHAR} TIME4 FROM dual)
) A

mapper.select:
SELECT A.PARAMS_NAME AS params_name, A.PARAMS_VALUE AS params_value
FROM API_PARAMS A
WHERE A.API_NAME = #{apiName}
