mapper.select:
select 

from REPORT_FLEETAREA


	and FLEETAREA_ID = #{fleetAreaId,jdbcType=DECIMAL}


	and AREA_OPERATING_UNIT_SHORTNAME = #{areaOperationUnitShortname,jdbcType=VARCHAR}


	and THE_FLEET_SHORTNAME = #{theFleetShortName,jdbcType=VARCHAR}


	and THE_AREA = #{theArea,jdbcType=VARCHAR}


	and THE_BUSINESS_DEPARTMENT = #{theBusinessDepartment,jdbcType=VARCHAR}


	and ACTIVE = #{active,jdbcType=VARCHAR}


order by CREATE_TIME desc

select count(*)
from REPORT_FLEETAREA


	and FLEETAREA_ID = #{fleetAreaId,jdbcType=DECIMAL}


	and AREA_OPERATING_UNIT_SHORTNAME = #{areaOperationUnitShortname,jdbcType=VARCHAR}


	and THE_FLEET_SHORTNAME = #{theFleetShortName,jdbcType=VARCHAR}


	and THE_AREA = #{theArea,jdbcType=VARCHAR}


	and THE_BUSINESS_DEPARTMENT = #{theBusinessDepartment,jdbcType=VARCHAR}


	and ACTIVE = #{active,jdbcType=VARCHAR}
